import LoginLayout from "../components/loginLayout";
import Link from "next/link";

function getPosts(){
    return[
        {id: "hello-nextjs", title: "Hello Next.js"},
        {id: "learn-nextjs", title: "Learn Next.js is awesome"},
        {id: "deploy-nextjs", title: "Deplot apps with ZEIT"}
    ]
}

const PostLink = ({ post }) => (
    <li key={post.id}>
        <Link as={`/p/${post.id}`} href={`/post?title=${post.title}`}>
            <a>{post.title}</a>
        </Link>
        <style jsx>{`
            li {
                list-style: none;
                margin: 5px 0;
            }

            a {
                text-decoration: none;
                color: blue;
                font-family: 'Arial';
                transition: .2s;
            }

            a:hover {
                opacity: 0.6;
            }
        `}</style>
    </li>
);

