export default function Blog(){
    return(
        <LoginLayout>
            <div>
                <img src="/Family.png"/>
            </div>
            <h1>My Blog</h1>
            <ul>
                {getPosts().map(post =>
                    <PostLink key={post.id} post={post}/>    
                )}
            </ul>
            <style jsx>{`
                h1,
                a {
                font-family: 'Arial';
                }

                ul {
                padding: 0;
                }

                li {
                list-style: none;
                margin: 5px 0;
                }

                a {
                text-decoration: none;
                color: blue;
                }

                a:hover {
                opacity: 0.6;
                }
            `}</style>
        </LoginLayout>
    );
}
